pub enum UrlGenerationError {
    ResourceNotFound,
    NotEnoughElements,
    ParseError(UrlParseError),
}

pub enum UrlencodedError {
    Chunked,
    Overflow {
        size: usize,
        limit: usize,
    },
    UnknownLength,
    ContentType,
    Parse(FormDeError),
    Encoding,
    Serialize(FormError),
    Payload(PayloadError),
}

pub enum JsonPayloadError {
    OverflowKnownLength {
        length: usize,
        limit: usize,
    },
    Overflow {
        limit: usize,
    },
    ContentType,
    Deserialize(JsonError),
    Serialize(JsonError),
    Payload(PayloadError),
}

pub enum ReadlinesError {
    EncodingError,
    Payload(PayloadError),
    LimitOverflow,
    ContentTypeError(ContentTypeError),
}

pub enum ContentRangeSpec {
    Bytes {
        range: Option<(u64, u64)>,
        instance_length: Option<u64>,
    },
    Unregistered {
        unit: String,
        resp: String,
    },
}

pub enum ConditionMiddlewareFuture<E, D> {
    Enabled {
        fut: E,
    },
    Disabled {
        fut: D,
    },
}

pub enum ErrorHandlersFuture<Fut, B> where Fut: Future {
    ServiceFuture {
        fut: Fut,
        handlers: Handlers<B>,
    },
    ErrorHandlerFuture {
        fut: LocalBoxFuture,
    },
}

pub enum JsonBody<T> {
    Error(Option<JsonPayloadError>),
    Body {
        limit: usize,
        length: Option<usize>,
        payload: Decompress<Payload>,
        buf: BytesMut,
        _res: PhantomData<T>,
    },
}
